CREATE TABLE IF NOT EXISTS countries_extended (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255),
    iso2 CHAR(2),
    iso3 CHAR(3),
    numeric_code VARCHAR(10),
    calling_code VARCHAR(20),
    currency VARCHAR(50),
    region VARCHAR(50),
    subregion VARCHAR(50),
    capital VARCHAR(255),
    timezone VARCHAR(100),
    languages TEXT,
    area DOUBLE,
    population BIGINT,
    flag VARCHAR(10),
    tld VARCHAR(10),
    continent VARCHAR(50)
);

